var path = require('path');
//mime获取

var mimedo = function(p){
  var z = path.extname(p);
  if(z=='' || z=='.'){z = '*'};
  return mimedo.k[z];
};

mimedo.k = {};

mimedo.set = function(m,n){
  mimedo.k['.'+m] = n;
};

