//mix obj混合
var mix = require('./lib/mix.js');
//main 主函数
var main = require('./lib/main.js');

exports = module.exports = start;

//主函数
function start (){
  //构造主函数
  var creat = function(req,res){
    //运行时函数
    creat.loop(req,res)
  };
  //混合
  mix(creat,main);
  //初始化
  creat.init();
  return creat;
}