var cookiedo = function(v,path,emp,httponly){
  if(httponly == true){httponly = 'HttpOnly'}else{httponly = ''};
  if(emp != ''){emp =' expires='+ new Date(((new Date()).getTime()+emp)).toGMTString()};  
  if(typeof(v) == 'string'){
    v = v+';Path='+path+';'+emp+';'+httponly;
    return v;
  };
  if(typeof(v) == 'object'){
    var nv = [];
    for (i = 0; i < v.length; i++){
      nv.push(v[i]+';Path='+path+';'+emp+';'+httponly);
    };
    return nv;
  };
};
exports = module.exports = cookiedo;